import React, { Component } from "react";
import SwordToken from "../contracts/SwordToken.json";
import NFT from "../contracts/ERC721Enumerable.json";
import getWeb3 from "../getWeb3";

//import "./App.css";

class TokenExplorer extends Component {
  state = { 
    storageValue: 0, 
    web3: null, 
    contractAddress: '0xED829b4000C72c57f7b7b11DA9b5706bAD9C9ec0', 
    network: 'kovan',
    contractToken: null,
    contractNFT: null, 
    supply: 0
  };

  componentDidMount = async () => {
    try {
      const web3 = await getWeb3();
      const accounts = await web3.eth.getAccounts();
      const token = new web3.eth.Contract(SwordToken, this.state.contractAddress);
      const nft = new web3.eth.Contract(NFT, this.state.contractAddress);
      // Set web3, accounts, and contract to the state, and then proceed with an
      // example of interacting with the contract's methods.
      //this.setState({ web3, accounts, contract: instance }, this.runExample);
      this.setState({ web3, accounts, contractToken: token, contractNFT: nft }); 
      this.setState(this.totalSupply);
      console.log(this.state.supply)
    } catch (error) {
      // Catch any errors for any of the above operations.
      alert(
        `Failed to load web3, accounts, or contract. Check console for details.`,
      );
      console.error(error);
    }
  };

  totalSupply = async () => {
    try {
      const { contractToken, web3 } = this.state;
      const supply = await contractToken.methods.totalSupply().call();
      console.log(supply._hex);
      var response;
      contractToken.methods.totalSupply().call()
        .then((res) => { response = web3.utils.hexToNumber(res._hex) })
        .then(console.log(response));
    } catch (error) {
      alert(`Failed to get toal SwordToken supply!`);
      console.log(error);
    }
  }

  runExample = async () => {
    const { accounts, contract } = this.state;

    // Stores a given value, 5 by default.
    await contract.methods.set(5).send({ from: accounts[0] });

    // Get the value from the contract to prove it worked.
    const response = await contract.methods.get().call();

    // Update state with the result.
    this.setState({ storageValue: response });
  };

  render() {
    if (!this.state.web3) {
      return <div>Loading Web3, accounts, and contract...</div>;
    }
    return (
      <div className="TokenExplorer">
        <h1>Good to Go!</h1>
        <p>Your Truffle Box is installed and ready.</p>
        <h2>Smart Contract Example</h2>
        <p>
          If your contracts compiled and migrated successfully, below will show
          a stored value of 5 (by default).
        </p>
        <p>
          Try changing the value stored on <strong>line 40</strong> of App.js.
        </p>

      </div>
    );
  }
}
//<div>The stored value is: {this.state.storageValue}</div>
export default TokenExplorer;
